catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var ga = new GlideAjax('x_1292527_lva.LVAUtils');
    ga.addParam('sysparm_name', 'dateToReturnVal');
    ga.addParam('sysparm_dateNeeded', g_form.getValue('date_needed'));
    ga.addParam('sysparm_dateToReturn', g_form.getValue('date_to_return_the_vehicle'));
	//alert('Thanuja: ' + g_form.getValue('date_to_return_the_vehicle'));
    ga.getXMLAnswer(callBackParse);

    function callBackParse(answer) {
        if (answer == 'false') {
            g_form.addErrorMessage('Date to return should not be before the Date needed');
            g_form.clearValue('date_to_return_the_vehicle');

        }

    }
}
