sys_script_include.script:
var LVAUtils = Class.create();
LVAUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    autoPopulateLocation: function() {
        var user_id = this.getParameter('sysparm_user_id');
        var response = '';
        var user = new GlideRecord('sys_user');
        user.addQuery('sys_id', user_id);
        user.query();
        while (user.next()) {
            if (user.location != '') {
                response = user.getValue('location');
            }
        }
        return response.toString();
    },

    dateToReturnVal: function() {
        try {
            var response = true;
            var dateToReturn = this.getParameter('sysparm_dateToReturn');
            var dateNeeded = this.getParameter('sysparm_dateNeeded');
            //gs.info('Date to Return= '+ dateToReturn + ' Date Needed= '+ dateNeeded);
            var returnDate = new GlideDate();
            returnDate.setValue(dateToReturn);
            var needDate = new GlideDate();
            needDate.setValue(dateNeeded);
            //gs.info('Date to Return= '+ dateToReturn + ' Date Needed= '+ dateNeeded + ' Return Date= ' + returnDate + 'Need Date= ' + needDate);

            if (returnDate < needDate) {
                response = false;
            }

            return response.toString();
        } catch (err) {
            //gs.info('Thanuja: ' + err);
        }


    },
    type: 'LVAUtils'
});
